import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import '../App.css';

const API_KEY = '27YlL+6OLO4xKhW87/Yjfw==eeqdTMN0BIh8F7Rg';

function Home() {
  const location = useLocation();
  const [make, setMake] = useState('');
  const [bikes, setBikes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    if (location.state?.brand) {
      setMake(location.state.brand);
    }
  }, [location.state]);

  useEffect(() => {
    if (make) {
      fetchBikes();
    }
  }, [make]);

  const fetchBikes = async () => {
    if (!make) return;
    setLoading(true);
    setError('');
    setBikes([]);

    try {
      const response = await fetch(
        `https://api.api-ninjas.com/v1/motorcycles?make=${make}`,
        {
          headers: { 'X-Api-Key': API_KEY },
        }
      );
      const data = await response.json();
      if (Array.isArray(data)) {
        setBikes(data.slice(0, 5));
      } else {
        setError('No data found.');
      }
    } catch (err) {
      setError('Error fetching data.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div
      style={{
        backgroundImage: 'url("/assets/background.jpg")',
        backgroundSize: 'contain',
        backgroundRepeat: 'no-repeat',
        backgroundPosition: 'center',
        minHeight: '100vh',
        position: 'relative',
      }}
    >
      {/* Dark overlay */}
      <div
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          height: '100%',
          width: '100%',
          backgroundColor: 'rgba(0, 0, 0, 0.6)',
          zIndex: 1,
        }}
      />

      {/* Page Content */}
      <div style={{ position: 'relative', zIndex: 2, padding: '30px', color: 'white' }}>
        <h1 style={{ textAlign: 'center', marginBottom: '20px', fontSize: '36px' }}>
          Search for any bike
        </h1>

        <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '20px' }}>
          <input
            type="text"
            placeholder="Enter bike name"
            value={make}
            onChange={(e) => setMake(e.target.value)}
            style={{
              width: '250px',
              padding: '10px',
              borderRadius: '25px',
              border: '1px solid #ccc',
              fontSize: '16px',
              marginRight: '10px',
            }}
          />
          <button
            onClick={fetchBikes}
            style={{
              padding: '10px 20px',
              backgroundColor: '#3498db',
              color: 'white',
              border: 'none',
              borderRadius: '25px',
              cursor: 'pointer',
              fontSize: '16px',
            }}
          >
            Search
          </button>
        </div>

        {loading && <p style={{ textAlign: 'center' }}>Loading...</p>}
        {error && <p style={{ textAlign: 'center', color: '#e74c3c' }}>{error}</p>}

        <div
          className="bike-list"
          style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center', gap: '20px' }}
        >
          {bikes.map((bike, index) => (
            <div
              key={index}
              className="bike-card"
              style={{
                backgroundColor: 'rgba(0, 0, 0, 0.7)',
                padding: '20px',
                borderRadius: '10px',
                width: '300px',
                color: '#fff',
              }}
            >
              <h2>{bike.make} {bike.model}</h2>
              <p><strong>Year:</strong> {bike.year}</p>
              <p><strong>Engine:</strong> {bike.engine}</p>
              <p><strong>Power:</strong> {bike.power}</p>
              <p><strong>Top Speed:</strong> {bike.top_speed} km/h</p>
              <p><strong>Torque:</strong> {bike.torque}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Home;
