import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import '../App.css';

const API_KEY_NINJAS = '27YlL+6OLO4xKhW87/Yjfw==eeqdTMN0BIh8F7Rg'; // API key for Ninjas API
const API_KEY_UNSPLASH = '6bLF7agOw-THLpKzeaD3r11CFNXhcZ-pxBMmpsPYvno'; // API key for Unsplash API

function Home() {
  const location = useLocation();
  const [make, setMake] = useState('');
  const [bikes, setBikes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Update make based on route state
  useEffect(() => {
    if (location.state?.brand) {
      setMake(location.state.brand);
    }
  }, [location.state]);

  // Fetch bikes data whenever the make changes
  useEffect(() => {
    if (make) {
      fetchBikes();
    }
  }, [make]);

  // Fetch the image from Unsplash API based on make and model
  const fetchBikeImage = async (make, model) => {
    try {
      const response = await fetch(
        `https://api.unsplash.com/search/photos?query=${make}+${model}&client_id=${API_KEY_UNSPLASH}`
      );
      const data = await response.json();
      return data.results[0]?.urls?.regular || 'https://via.placeholder.com/150?text=No+Image'; // Default image if no result
    } catch (err) {
      console.error('Error fetching image:', err);
      return 'https://via.placeholder.com/150?text=No+Image'; // Fallback image on error
    }
  };

  // Fetch bikes data from the API Ninjas Motorcycles API and also get images from Unsplash
  const fetchBikes = async () => {
    if (!make) return;
    setLoading(true);
    setError('');
    setBikes([]);

    try {
      const response = await fetch(
        `https://api.api-ninjas.com/v1/motorcycles?make=${make}`,
        {
          headers: { 'X-Api-Key': API_KEY_NINJAS },
        }
      );
      const data = await response.json();
      if (Array.isArray(data)) {
        // Fetch images for each bike
        const bikesWithImages = await Promise.all(
          data.map(async (bike) => {
            const image = await fetchBikeImage(bike.make, bike.model); // Fetch image from Unsplash
            return {
              ...bike,
              image, // Add the image URL to the bike data
              numericPower: parseInt(bike.power.match(/\d+/)?.[0]) || 0, // Extract numeric power
            };
          })
        );

        // Sort bikes based on power and take the top 5
        setBikes(bikesWithImages.sort((a, b) => b.numericPower - a.numericPower).slice(0, 5));
      } else {
        setError('No data found.');
      }
    } catch (err) {
      setError('Error fetching data.');
    } finally {
      setLoading(false);
    }
  };

  // Open Wikipedia page for the bike
  const openWikiPage = (make, model) => {
    const query = `${make} ${model}`.replace(/\s+/g, '+');
    const url = `https://en.wikipedia.org/wiki/Special:Search?search=${query}`;
    window.open(url, '_blank');
  };

  // Render the bike details in a card
  const renderCard = (bike, index) => (
    <div
      key={index}
      onClick={() => openWikiPage(bike.make, bike.model)}
      style={{
        backgroundColor: 'rgba(255,255,255,0.1)',
        padding: '20px',
        borderRadius: '12px',
        cursor: 'pointer',
        transition: 'transform 0.2s, background-color 0.2s',
      }}
      onMouseEnter={(e) => e.currentTarget.style.backgroundColor = 'rgba(255,255,255,0.2)'}
      onMouseLeave={(e) => e.currentTarget.style.backgroundColor = 'rgba(255,255,255,0.1)'}
    >
      {/* Image */}
      <img
        src={bike.image}
        alt={`${bike.make} ${bike.model}`}
        style={{
          width: '100%',
          height: 'auto',
          borderRadius: '10px',
          marginBottom: '15px',
        }}
      />
      <h2>{bike.make} {bike.model}</h2>
      <p><strong>Year:</strong> {bike.year}</p>
      <p><strong>Engine:</strong> {bike.engine}</p>
      <p><strong>Power:</strong> {bike.power}</p>
      <p><strong>Top Speed:</strong> {bike.top_speed} km/h</p>
      <p><strong>Torque:</strong> {bike.torque}</p>
    </div>
  );

  return (
    <div
      style={{
        position: 'relative',
        backgroundImage: "url('/assets/background.jpg')",
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        minHeight: '100vh',
        color: 'white',
      }}
    >
      {/* Dark Overlay */}
      <div
        style={{
          position: 'absolute',
          top: 0,
          bottom: 0,
          left: 0,
          right: 0,
          backgroundColor: 'rgba(0, 0, 0, 0.6)',
          zIndex: 1,
        }}
      ></div>

      {/* Content */}
      <div
        style={{
          position: 'relative',
          zIndex: 2,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          paddingTop: '60px',
          paddingLeft: '20px',
          paddingRight: '20px',
        }}
      >
        <h1 style={{ fontSize: '40px', marginBottom: '20px' }}>
          Search for any bike
        </h1>

        {/* Search Input */}
        <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
          <input
            type="text"
            placeholder="Enter bike name"
            value={make}
            onChange={(e) => setMake(e.target.value)}
            style={{
              padding: '12px 20px',
              fontSize: '16px',
              borderRadius: '30px',
              border: '1px solid #ccc',
              marginRight: '10px',
              backgroundColor: 'rgba(255, 255, 255, 0.1)',
              color: '#fff',
              outline: 'none',
            }}
          />
          <button
            onClick={fetchBikes}
            style={{
              padding: '12px 24px',
              fontSize: '16px',
              borderRadius: '30px',
              border: 'none',
              backgroundColor: '#3498db',
              color: 'white',
              cursor: 'pointer',
              marginTop: '5px',
            }}
          >
            Search
          </button>
        </div>

        {loading && <p style={{ marginTop: '20px' }}>Loading...</p>}
        {error && <p style={{ color: 'red', marginTop: '20px' }}>{error}</p>}

        {/* Grid 3x2 Layout */}
        <div
          style={{
            marginTop: '40px',
            display: 'grid',
            gridTemplateColumns: 'repeat(3, 1fr)',
            gap: '20px',
            width: '100%',
            maxWidth: '1200px',
          }}
        >
          {bikes.slice(0, 3).map(renderCard)}
        </div>

        <div
          style={{
            marginTop: '20px',
            display: 'grid',
            gridTemplateColumns: 'repeat(2, 1fr)',
            gap: '20px',
            width: '100%',
            maxWidth: '800px',
          }}
        >
          {bikes.slice(3, 5).map(renderCard)}
        </div>
      </div>
    </div>
  );
}

export default Home;
