import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import '../App.css'; // Reuse existing styles

const API_KEY = '27YlL+6OLO4xKhW87/Yjfw==eeqdTMN0BIh8F7Rg';

function Home() {
  const location = useLocation();
  const [make, setMake] = useState('');
  const [bikes, setBikes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    if (location.state?.brand) {
      setMake(location.state.brand);
    }
  }, [location.state]);

  useEffect(() => {
    if (make) {
      fetchBikes();
    }
  }, [make]);

  const fetchBikes = async () => {
    if (!make) return;
    setLoading(true);
    setError('');
    setBikes([]);

    try {
      const response = await fetch(
        `https://api.api-ninjas.com/v1/motorcycles?make=${make}`,
        {
          headers: { 'X-Api-Key': API_KEY },
        }
      );
      const data = await response.json();
      if (Array.isArray(data)) {
        setBikes(data.slice(0, 5));
      } else {
        setError('No data found.');
      }
    } catch (err) {
      setError('Error fetching data.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app-container">
      {/* Centered "Search for any bike" text with increased font size */}
      <h1 style={{ textAlign: 'center', marginBottom: '10px', fontSize: '36px' }}>
        Search for any bike
      </h1>
      
      <div className="search-section" style={{ display: 'flex', justifyContent: 'center', marginBottom: '15px' }}>
        <input
          type="text"
          placeholder="Enter bike name"
          value={make}
          onChange={(e) => setMake(e.target.value)}
          style={{
            width: '250px',
            padding: '10px',
            borderRadius: '25px', // More rounded search bar
            border: '1px solid #ccc',
            fontSize: '16px',
            marginRight: '10px',
          }}
        />
        <button
          onClick={fetchBikes}
          style={{
            padding: '10px 20px',
            backgroundColor: '#3498db',
            color: 'white',
            border: 'none',
            borderRadius: '25px', // Rounded button
            cursor: 'pointer',
            fontSize: '16px',
            outline: 'none', // Remove the blue outline
          }}
        >
          Search
        </button>
      </div>

      {loading && <p className="loading" style={{ textAlign: 'center' }}>Loading...</p>}
      {error && <p className="error" style={{ textAlign: 'center', color: '#e74c3c' }}>{error}</p>}

      <div className="bike-list">
        {bikes.map((bike, index) => (
          <div key={index} className="bike-card">
            <h2>{bike.make} {bike.model}</h2>
            <p><strong>Year:</strong> {bike.year}</p>
            <p><strong>Engine:</strong> {bike.engine}</p>
            <p><strong>Power:</strong> {bike.power}</p>
            <p><strong>Top Speed:</strong> {bike.top_speed} km/h</p>
            <p><strong>Torque:</strong> {bike.torque}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
