import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import '../App.css'; // Assuming global styles

const API_KEY = '27YlL+6OLO4xKhW87/Yjfw==eeqdTMN0BIh8F7Rg';

function Home() {
  const location = useLocation();
  const [make, setMake] = useState('');
  const [bikes, setBikes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    if (location.state?.brand) {
      setMake(location.state.brand);
    }
  }, [location.state]);

  useEffect(() => {
    if (make) {
      fetchBikes();
    }
  }, [make]);

  const fetchBikes = async () => {
    if (!make) return;
    setLoading(true);
    setError('');
    setBikes([]);

    try {
      const response = await fetch(
        `https://api.api-ninjas.com/v1/motorcycles?make=${make}`,
        {
          headers: { 'X-Api-Key': API_KEY },
        }
      );
      const data = await response.json();
      if (Array.isArray(data)) {
        setBikes(data.slice(0, 5));
      } else {
        setError('No data found.');
      }
    } catch (err) {
      setError('Error fetching data.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div
      className="app-container"
      style={{
        position: 'relative',
        backgroundImage: "url('/assets/background.jpg')",
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        minHeight: '100vh',
        color: 'white',
      }}
    >
      {/* Overlay */}
      <div
        style={{
          position: 'absolute',
          top: 0,
          bottom: 0,
          left: 0,
          right: 0,
          backgroundColor: 'rgba(0, 0, 0, 0.6)',
          zIndex: 1,
        }}
      ></div>

      {/* Main content */}
      <div style={{ position: 'relative', zIndex: 2, paddingTop: '40px' }}>
        <h1 style={{ textAlign: 'center', marginBottom: '10px', fontSize: '36px' }}>
          Search for any bike
        </h1>

        <div
          className="search-section"
          style={{
            display: 'flex',
            justifyContent: 'center',
            marginBottom: '15px',
          }}
        >
          <input
            type="text"
            placeholder="Enter bike name"
            value={make}
            onChange={(e) => setMake(e.target.value)}
            style={{
              width: '250px',
              padding: '10px',
              borderRadius: '25px',
              border: '1px solid #ccc',
              fontSize: '16px',
              marginRight: '10px',
              backgroundColor: 'rgba(255, 255, 255, 0.1)',
              color: 'white',
            }}
          />
          <button
            onClick={fetchBikes}
            style={{
              padding: '10px 20px',
              backgroundColor: '#3498db',
              color: 'white',
              border: 'none',
              borderRadius: '25px',
              cursor: 'pointer',
              fontSize: '16px',
              outline: 'none',
            }}
          >
            Search
          </button>
        </div>

        {loading && (
          <p className="loading" style={{ textAlign: 'center' }}>
            Loading...
          </p>
        )}
        {error && (
          <p className="error" style={{ textAlign: 'center', color: '#e74c3c' }}>
            {error}
          </p>
        )}

        <div className="bike-list" style={{ padding: '0 20px' }}>
          {bikes.map((bike, index) => (
            <div
              key={index}
              className="bike-card"
              style={{
                backgroundColor: 'rgba(255,255,255,0.1)',
                padding: '20px',
                borderRadius: '10px',
                marginBottom: '20px',
              }}
            >
              <h2>{bike.make} {bike.model}</h2>
              <p><strong>Year:</strong> {bike.year}</p>
              <p><strong>Engine:</strong> {bike.engine}</p>
              <p><strong>Power:</strong> {bike.power}</p>
              <p><strong>Top Speed:</strong> {bike.top_speed} km/h</p>
              <p><strong>Torque:</strong> {bike.torque}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Home;
